import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import { fetchStudents } from '../services/api';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const { logout } = useAuth();
  const { darkMode, toggleTheme } = useTheme();
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showActive, setShowActive] = useState(false);

  useEffect(() => {
    const getStudents = async () => {
      try {
        setLoading(true);
        const data = await fetchStudents();
        setStudents(data);
        setError(null);
      } catch (err) {
        setError('Failed to fetch student data. Please try again.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    getStudents();
  }, []);

  // Filter students based on active status if filter is applied
  const filteredStudents = showActive 
    ? students.filter(student => student.active) 
    : students;

  // Calculate statistics
  const totalStudents = filteredStudents.length;
  const activeStudents = filteredStudents.filter(student => student.active).length;
  const averageAge = totalStudents 
    ? Math.round(filteredStudents.reduce((sum, student) => sum + student.age, 0) / totalStudents) 
    : 0;

  // Prepare chart data
  const courseData = filteredStudents.reduce((acc, student) => {
    const existingCourse = acc.find(item => item.name === student.course);
    if (existingCourse) {
      existingCourse.count++;
    } else {
      acc.push({ name: student.course, count: 1 });
    }
    return acc;
  }, []);

  const statusData = [
    { name: 'Active', value: activeStudents },
    { name: 'Inactive', value: totalStudents - activeStudents }
  ];

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];
  const STATUS_COLORS = ['#00C49F', '#FF8042'];

  return (
    <div className={`dashboard-container ${darkMode ? 'dark-theme' : 'light-theme'}`}>
      <header className="dashboard-header">
        <h1>Student Data Dashboard</h1>
        <div className="header-controls">
          <button onClick={toggleTheme} className="theme-toggle">
            {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
          </button>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </header>

      {loading ? (
        <div className="loading">Loading student data...</div>
      ) : error ? (
        <div className="error-message">{error}</div>
      ) : (
        <>
          <div className="dashboard-controls">
            <label>
              <input 
                type="checkbox" 
                checked={showActive} 
                onChange={() => setShowActive(!showActive)} 
              />
              Show only active students
            </label>
          </div>

          <div className="stats-container">
            <div className="stat-card">
              <h3>Total Students</h3>
              <p>{totalStudents}</p>
            </div>
            <div className="stat-card">
              <h3>Active Students</h3>
              <p>{activeStudents}</p>
            </div>
            <div className="stat-card">
              <h3>Average Age</h3>
              <p>{averageAge}</p>
            </div>
          </div>

          <div className="charts-container">
            <div className="chart-card">
              <h3>Students per Course</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={courseData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="count" fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            <div className="chart-card">
              <h3>Active vs. Inactive Students</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={statusData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value }) => `${name}: ${value}`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {statusData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={STATUS_COLORS[index % STATUS_COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>

          <div className="table-container">
            <h3>Student List</h3>
            <table className="student-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Age</th>
                  <th>Course</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {filteredStudents.map(student => (
                  <tr key={student._id || student.id}>
                    <td>{student._id || student.id}</td>
                    <td>{student.name}</td>
                    <td>{student.age}</td>
                    <td>{student.course}</td>
                    <td className={student.active ? 'active-status' : 'inactive-status'}>
                      {student.active ? 'Active' : 'Inactive'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
